export const bugAdded = (description) => ({
  type: actions.BUG_ADDED,
  payload: {
    description,
  },
});

export const bugResolved = (id) => ({
  type: actions.BUG_RESOLVED,
  payload: {
    id,
  },
});
export const errorOccurred = (id) => ({
  type: actions.ERROR_OCCURRED,
  payload: {
    message:"An error occurred",
  },
});
