import { createStore, applyMiddleware } from "redux";
import reducer from "./reducer";
import logger from "./store/middleware/logger";

//always pass a refernce of the function donot call it i.e. reducer()
// this is how a middleware is applied without using redux toolkit
// const store = createStore(reducer, applyMiddleware(logger));
//REDUX TOOLKIT (simplifies REDUX CODE)
import { configureStore, getDefaultMiddleware } from "@reduxjs/toolkit";
const store = configureStore({
  reducer,
  middleware: [...getDefaultMiddleware(), logger("console ^_^")],
});
export default store;
