const displayToast =
  (store) =>
  (next) =>
  (action) => {
    if (action.type == "error") {
      console.log("Error:", action.payload.message);
    }
    // else pass it to the next middleware function or the reducer
    else {
      next();
    }
  };
export default displayToast;
