import axios from "axios";

const api = (store) => (next) => (action) => {
    if (action.type !== 'apiCall') return next(action);
    const { url, method, data, onSucces, onError } = action;
    axios.request({
        baseURL:"http://localhost:9001/api",
        url,
        method,
        data,
    }).then()
};
export default api;
