import reducer from "./reducer";
import logger from "./middleware/logger";
import displayToast from "./middleware/displayToast";
import { configureStore, getDefaultMiddleware } from "@reduxjs/toolkit";
/**
 * 
 * @returns 
 */
export default function () {
  // Always pass a refernce of the function donot call it i.e. write 'reducer' rather than 'reducer()'
  return configureStore({
    reducer,
    middleware: [
      ...getDefaultMiddleware(),
      displayToast,
      logger({ test: "1" }),
    ], // Note: the order of middlewares matter & getDefaultMiddleware gets an array of all the middlewares && spread operator is used to copy them
  });
}
