const func = (dispatch, getState) => (next) => (action) => {
  // if the action that is dispatched is a function then execute that function
  if (typeof action == "function") {
    action();
  }
  // else pass it to the next middleware function or the reducer
  else {
    next(action);
  }
};
export default func;
